import { Alert } from 'bootstrap';
import { Form, Button } from 'react-bootstrap';
import { useInput } from './hooks/input-hook';

function AddUser ({  SERVICE_URL, handleClose }) {
  const { value:username, bind:bindUsername, reset:resetUsername } = useInput('');
  const { value:password, bind:bindPassword, reset:resetPassword } = useInput('');
  const { value:password2, bind:bindPassword2, reset:resetPassword2 } = useInput('');
  const validateForm = (user) => {
    let errors = {isValid: true};

    if (!user.username) {
      errors.username = "Please enter a username";
      errors.isValid = false;
    }

    if(!user.password) {
      errors.password = "Please enter a password"
      errors.isValid = false;
    } else if (user.password !== user.password2){
      errors.password = "Passwords do not match."
      errors.isValid = true;
    }

    return errors;
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    const user = {
        "name": username,
        "password": password
    }
    console.log(user);

    let validationErrors = validateForm({...user, password2});
    if (!validationErrors.isValid) {
      Alert(validationErrors.username, validationErrors.password);
      return;
    }


    fetch(SERVICE_URL + '/AdminPortal/CreateUser', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(user),
    })
    .then(response => response.json())
    .then(data => {
      console.log('Add User - Success:', data);
      //setMarkers([...markers, user]);
      handleClose();
    })
    .catch((error) => {
      console.log('Add User - Error:', error);
    });


    resetUsername();
    resetPassword();
    resetPassword2();
  }

  return (
    <div>
      <Form onSubmit={handleSubmit}>
                  <Form.Group controlId="username">
                      <Form.Label>Username:</Form.Label>
                      <Form.Control type="text" placeholder="Username" {...bindUsername} required/>
                  </Form.Group>
                  <Form.Group controlId="password1">
                      <Form.Label>Password:</Form.Label>
                      <Form.Control type="password" placeholder="Password" {...bindPassword} required/>
                  </Form.Group>
                  <Form.Group controlId="password2">
                      <Form.Label>Re-enter Password:</Form.Label>
                      <Form.Control type="password" placeholder="Re-Enter Password" {...bindPassword2} />
                  </Form.Group>
                  <Button variant="primary" type="submit">
                      Submit
                  </Button>
      </Form>
    </div>
  );
}

export default AddUser;
