import './App.css';
import React, {useState, useRef, useCallback} from 'react';
import { GoogleMap, useLoadScript, Marker, InfoWindow } from '@react-google-maps/api';
import mapStyles from './mapStyles';
import Search from './Search';
import Locate from './Locate';
import {
  getGeocode,
  getLatLng
} from "use-places-autocomplete";

const SERVICE_URL = "http://localhost:8080/VaccTrak";

const libraries = ["places"];
const mapContainerStyle = {
  width: '100vw',
  height: '100vh'
};
const center = {
  lat: 37.090240,
  lng: -95.712891
}
const options = {
  styles: mapStyles,
  disableDefaultUI: true,
  zoomControl: true
}

const markers = [
  {
    vacCenter: "Chicago",
    numFirstVaccine: 100,
    numSecondVaccine: 100,
    lat: 41.878113,
    lng: -87.629799
  },
  {
    vacCenter: "New Jersey",
    numFirstVaccine: 200,
    numSecondVaccine: 100,
    lat: 40.073040,
    lng: -74.724319
  }
]


function App() {
  const {isLoaded, loadError} = useLoadScript({
    googleMapsApiKey: "AIzaSyAp1BrMA7-YofbC3iJapScTWCpI8GmZm-w",
    libraries: libraries
  })
  const [selected, setSelected] = useState(null);

  const mapRef = useRef();
  const onMapLoad = useCallback(
    (map) => {
      mapRef.current = map
      fetch(SERVICE_URL + "/")
    },
    []
  )

  const panTo = useCallback(
  ({lat, lng}) => {
    mapRef.current.panTo({lat, lng});
    mapRef.current.setZoom(14);
  },
  [],
  )

  const getLatLngFromAddress = async (address) => {
        try {
          const results = await getGeocode({address});
          const { lat, lng } = await getLatLng(results[0]);
          return ({lat, lng})
        } catch(error) {
          console.log("error1");
        }
  };

  if (loadError) return "Error loading maps";
  if (!isLoaded) return "Loading maps";

  return (<div>

    <img src="logo.png" alt="logo" id="logo"/>
    <Search panTo={panTo} getLatLngFromAddress={getLatLngFromAddress}/>
    <Locate panTo={panTo} />

    <GoogleMap 
    mapContainerStyle={mapContainerStyle}
    zoom={4.7}
    center={center}
    options={options}
    onLoad={onMapLoad}>
      {markers.map((marker, i) => 
        <Marker 
          key={i} 
          position={{lat: marker.lat, lng: marker.lng}}
          onClick={() => {
            setSelected(marker);
          }}
        />
      )}

      {selected ? (
        <InfoWindow 
        position={{lat: selected.lat, lng: selected.lng}} 
        onCloseClick={() => setSelected(null)}>
          <div>
            <h3>{selected.vacCenter}</h3>
            <div>
              <div>
                1st Dose: {selected.numFirstVaccine}
              </div>
              <div>
                2nd Dose: {selected.numSecondVaccine}
              </div>
              <hr />
              Total Vaccinated: {selected.numFirstVaccine + selected.numSecondVaccine}
            </div>
          </div>
        </InfoWindow>
      ) : null}
    </GoogleMap>
  </div>)

}

export default App;
