import logo from './logo.svg';
import './App.css';
import React, {useState, useRef, useCallback} from 'react';
import { GoogleMap, useLoadScript, Marker, InfoWindow } from '@react-google-maps/api';
import mapStyles from './mapStyles';
// import Search from './Search';
import usePlacesAutocomplete, {
  getGeocode,
  getLatLng
} from "use-places-autocomplete";
import {
  Combobox,
  ComboboxInput,
  ComboboxPopover,
  ComboboxOption
} from '@reach/combobox';
import '@reach/combobox/styles.css';

const libraries = ["places"];
const mapContainerStyle = {
  width: '100vw',
  height: '100vh'
};
const center = {
  lat: 37.090240,
  lng: -95.712891
}
const options = {
  styles: mapStyles,
  disableDefaultUI: true,
  zoomControl: true
}

const markers = [
  {
    vacCenter: "Chicago",
    numFirstVaccine: 100,
    numSecondVaccine: 100,
    lat: 41.878113,
    lng: -87.629799
  },
  {
    vacCenter: "New Jersey",
    numFirstVaccine: 200,
    numSecondVaccine: 100,
    lat: 40.073040,
    lng: -74.724319
  }
]


function App() {
  const {isLoaded, loadError} = useLoadScript({
    googleMapsApiKey: "AIzaSyCN-iHvhyXgbF55JP_rCyTT8cdaOpkoM_Q",
    libraries: libraries
  })
  const [selected, setSelected] = useState(null);

  const mapRef = useRef();
  const onMapLoad = useCallback(
    (map) => {
      mapRef.current = map
    },
    []
  )

  const panTo = useCallback(
  ({lat, lng}) => {
    mapRef.current.panTo({lat, lng});
    mapRef.current.setZoom(10);
  },
  [],
  )

  const getLatLngFromAddress = async (address) => {
        try {
          const results = await getGeocode({address});
          const { lat, lng } = await getLatLng(results[0]);
          return ({lat, lng})
        } catch(error) {
          console.log("error1");
        }
  };

  if (loadError) return "Error loading maps";
  if (!isLoaded) return "Loading maps";

  return (<div>

    <Search panTo={panTo} getLatLngFromAddress={getLatLngFromAddress}/>

    <GoogleMap 
    mapContainerStyle={mapContainerStyle}
    zoom={4.7}
    center={center}
    options={options}
    onLoad={onMapLoad}>
      {markers.map((marker, i) => 
        <Marker 
          key={i} 
          position={{lat: marker.lat, lng: marker.lng}}
          onClick={() => {
            setSelected(marker);
          }}
        />
      )}

      {selected ? (
        <InfoWindow 
        position={{lat: selected.lat, lng: selected.lng}} 
        onCloseClick={() => setSelected(null)}>
          <div>
            <h3>{selected.vacCenter}</h3>
            <div>
              <div>
                1st Dose: {selected.numFirstVaccine}
              </div>
              <div>
                2nd Dose: {selected.numSecondVaccine}
              </div>
              <hr />
              Total Vaccinated: {selected.numFirstVaccine + selected.numSecondVaccine}
            </div>
          </div>
        </InfoWindow>
      ) : null}
    </GoogleMap>
  </div>)

}

function Search({getLatLngFromAddress, panTo}) {
  const {
    ready, 
    value, 
    suggestions: {status, data}, 
    setValue, 
    clearSuggestions
  } = usePlacesAutocomplete({
    requestOptions: {
      location: {
        lat: () => 41.878113,
        lng: () => -87.629799,
      },
      radius: 200 * 1000
    }
  });
  return (
    <div className="search">
    <Combobox onSelect={async (address) => {
      try {
        const results = await getGeocode({address});
        console.log(results);
        //const { lat, lng } = await getLatLng(results[0]);
        //panTo({lat, lng});
      } catch (error) {
        console.log("error2");
      }
      }}>
      <ComboboxInput 
      value={value} 
      onChange={(e) => setValue(e.target.value)}
      disabled={!ready}
      placeholder="Enter an address"
      />
      <ComboboxPopover>
        {status === "OK" && data.map(({id, description})=> (<ComboboxOption key={id} value={description}/>))}
      </ComboboxPopover>
    </Combobox>
  </div>
  );
}

export default App;
